mapper.update:
UPDATE MEMBER
SET
	MEMBER_NAME=#{memberName}
  , MEMBER_PASSWORD=#{memberPassword}
  , PHONE_NUMBER=#{phoneNumber}
  , EMAIL=#{email}
  , MODIFIER=#{memberId}
  , MODIFY_DATE=SYSDATE
WHERE
	MEMBER_ID=#{memberId} AND MEMBER_STATUS='Y'

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
