mapper.update:
UPDATE MEMBER
SET
	MEMBER_NAME=#{memberName}
  , MEMBER_PASSWORD=#{memberPassword}
  , PHONE_NUMBER=#{phoneNumber}
  , EMAIL=#{email}
  , MODIFIER=#{memberId}
  , MODIFY_DATE=SYSDATE
WHERE
	MEMBER_ID=#{memberId} AND MEMBER_STATUS='Y'

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

		select count(*)
        from follow
        where to_id=#{memberId}

		select count(*)
        from follow
        where from_id=#{memberId}

		select follow_status
        from follow
        where to_id=#{memberId} and from_id=#{sessionId}

		select alarm_code as alarmCode, alarm_to_id as alarmToId, alarm_from_id as alarmFromId, alarm_status as alarmStatus
        from alarm
        where alarm_to_id=#{sessionId}
