mapper.select:
		select rnum, place_name as placeName , area_name as areaName , detail_cd_name as category,file_saved_name as fileSaved_name
		from (
			select ROWNUM as rnum, place_name  , area_name  , detail_cd_name,file_saved_name
			from(
				select m.place_name,m.area_name,d.detail_cd_name,m.file_saved_name
				from(select distinct place_name, area_name , category , file_saved_name
    				from place full outer join upload_file
    				on place.place_name = upload_file.file_code
    				)m, detail_code d
				where m.category=d.detail_cd_id
				order by m.place_name	
			)
		)

		SELECT rnum, file_saved_name as fileSavedName, travel_id as travelId, travel_title as travelTitle, free as free, view_cnt as viewCnt, share_cnt as shareCnt, writer as writer, write_date as writeDate, original_Travel_Id as originalTravelId
		FROM (
		  SELECT ROWNUM as rnum, file_saved_name, travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date, original_Travel_Id
		  FROM (

		    select c.file_saved_name, c.travel_id, c.travel_title, c.free, c.view_cnt, c.share_cnt, c.writer, c.write_date,
			(select count(*) from travel where original_travel_id like c.travel_id) as original_Travel_Id
			from 
			(select f.file_saved_name, b.travel_id, b.travel_title, b.free, b.view_cnt, b.share_cnt, b.writer, b.write_date
					from upload_file f, 
   					 (select p.place_name, a.travel_id, a.travel_title, a.free, a.view_cnt, a.share_cnt, a.writer, a.write_date
    					  from place p, 
      				  		(select t.travel_id, d.place_id, t.travel_title, t.free, t.view_cnt, t.share_cnt, t.writer, t.write_date
     				  			 from travel t, travel_detail d
        			  			 where t.travel_id=d.travel_id) a
  			 		 where p.place_id=a.place_id) b 
			where f.file_code like '%'||b.place_name||'%')c     

		  )
		  WHERE rownum <= #{endRowNo}  
		)
		WHERE rnum >= #{startRowNo}

SELECT rnum, file_saved_name as fileSavedName, travel_id as travelId, travel_title as travelTitle, free as free, view_cnt as viewCnt, share_cnt as shareCnt, writer as writer, write_date as writeDate, original_Travel_Id as originalTravelId FROM ( SELECT ROWNUM as rnum, file_saved_name, travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date, original_Travel_Id FROM (
select c.file_saved_name, c.travel_id, c.travel_title, c.free, c.view_cnt, c.share_cnt, c.writer, c.write_date, (select count(*) from travel where original_travel_id like c.travel_id) as original_Travel_Id from (select f.file_saved_name, b.travel_id, b.travel_title, b.free, b.view_cnt, b.share_cnt, b.writer, b.write_date from upload_file f, (select p.place_name, a.travel_id, a.travel_title, a.free, a.view_cnt, a.share_cnt, a.writer, a.write_date from place p, (select t.travel_id, d.place_id, t.travel_title, t.free, t.view_cnt, t.share_cnt, t.writer, t.write_date from travel t, travel_detail d where t.travel_id=d.travel_id) a where p.place_id=a.place_id) b where f.file_code like '%'||b.place_name||'%')c ORDER BY c.view_cnt desc
) WHERE rownum <= #{endRowNo} ) WHERE rnum >= #{startRowNo}

