SELECT rn, travel_id as travelId, travel_title as travelTitle, free as free, view_cnt as viewCnt, share_cnt as shareCnt, writer as writer, write_date as writeDate
FROM (select rownum as rn, travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
		FROM( 
		select travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
		FROM travel
		)
	  WHERE rownum <=#{endRowNo}
	  )
WHERE rn >=#{startRowNo}

		SELECT rn, travel_id as travelId, travel_title as travelTitle, free as free, view_cnt as viewCnt, share_cnt as shareCnt, writer as writer, write_date as writeDate
		FROM (select rownum as rn, travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
				FROM( 
				select travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
				FROM travel
                ORDER BY view_cnt desc
				)
			  WHERE rownum <=#{endRowNo}
			  )
		WHERE rn >=#{startRowNo}

		SELECT rn, travel_id as travelId, travel_title as travelTitle, free as free, view_cnt as viewCnt, share_cnt as shareCnt, writer as writer, write_date as writeDate
		FROM (select rownum as rn, travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
				FROM( 
				select travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
				FROM travel
                ORDER BY share_cnt desc
				)
			  WHERE rownum <=#{endRowNo}
			  )
		WHERE rn >=#{startRowNo}

		SELECT rn, travel_id as travelId, travel_title as travelTitle, free as free, view_cnt as viewCnt, share_cnt as shareCnt, writer as writer, write_date as writeDate
		FROM (select rownum as rn, travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
				FROM( 
				select travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
				FROM travel
                ORDER BY write_date desc
				)
			  WHERE rownum <=#{endRowNo}
			  )
		WHERE rn >=#{startRowNo}

SELECT rn, travel_id as travelId, travel_title as travelTitle, free as free, view_cnt as viewCnt, share_cnt as shareCnt, writer as writer, write_date as writeDate
FROM (select rownum as rn, travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
		FROM( 
		select travel_id, travel_title, free, view_cnt, share_cnt, writer, write_date
		FROM travel


		 AND travel_title like '%'||#{keyword}||'%'


		 AND writer like '%'||#{keyword}||'%'


		)WHERE rownum <=#{endRowNo}
	  )
WHERE rn >=#{startRowNo}
